    scanf("%d%d%d%d", &n, &m, &S, &T);
    int a, b, c, d;
    tot = 1;
    for (int i = 1; i <= m; i++) {
        scanf("%d%d%d%d", &a, &b, &c, &d);
        add_edge(a, b, d - c);
        A[a] -= c, A[b] += c;
    }

    int fullflow = 0;
    s = n + 1, t = n + 2;

    for (int i = 1; i <= n; i++) {
        if (A[i] > 0) {
            add_edge(s, i, A[i]);
            fullflow += A[i];
        } else if (A[i] < 0) {
            add_edge(i, t, -A[i]);
        }
    }

    add_edge(T, S, INF);

    if(Dinic() != fullflow) puts("No Solution");
    else{
        s = S, t = T;
        int res = cap[tot];
        cap[tot] = cap[tot - 1] = 0;
        printf("%d\n", res + Dinic());
    }
    return 0;
}
```

