### 2. 区间加法， 乘法， 区间求和

```cpp
struct SegmentTree {
    LL sumv[maxn], addv[maxn], mulv[maxn];

    void maintain(int o) {
        sumv[o] = sumv[lc] + sumv[rc];
    }

    void pushdown(int o, int L, int R) {
        int M = L + R >> 1;
        if (mulv[o] != 1) {
            mulv[lc] *= mulv[o];
            addv[lc] *= mulv[o];
            sumv[lc] *= mulv[o];

            mulv[rc] *= mulv[o];
            addv[rc] *= mulv[o];
            sumv[rc] *= mulv[o];

            mulv[o] = 1;
        }
        if (addv[o]) {
            addv[lc] += addv[o];
            sumv[lc] += addv[o] * (M - L + 1);

            addv[rc] += addv[o];
            sumv[rc] += addv[o] * (R - M);

            addv[o] = 0;
        }
    }

    void build(int o, int L, int R) {
        addv[o] = 0;
        mulv[o] = 1;
        if (L == R) {
            sumv[o] = v[L];
            return;
        }
        int M = L + R >> 1;
        build(lc, L, M);
        build(rc, M + 1, R);
        maintain(o);
    }

    LL query(int o, int L, int R, int l, int r) {
        if (l <= L && r >= R) return sumv[o];
        pushdown(o, L, R);
        int M = L + R >> 1;
        LL sum = 0;
        if (l <= M) sum += query(lc, L, M, l, r);
        if (r > M) sum += query(rc, M + 1, R, l, r);
        return sum;
    }

    void update_add(int o, int L, int R, int l, int r, int x) {
        if (l <= L && r >= R) {
            addv[o] += x;
            sumv[o] += x * (R - L + 1);
            return;
        }
        pushdown(o, L, R);
        int M = L + R >> 1;
        if (l <= M) update_add(lc, L, M, l, r, x);
        if (r > M) update_add(rc, M + 1, R, l, r, x);
        maintain(o);
    }

    void update_mul(int o, int L, int R, int l, int r, int x) {
        if (l <= L && r >= R) {
            mulv[o] *= x;
            addv[o] *= x;
            sumv[o] *= x;
            return;
        }
        pushdown(o, L, R);
        int M = L + R >> 1;
        if (l <= M) update_mul(lc, L, M, l, r, x);
        if (r > M) update_mul(rc, M + 1, R, l, r, x);
        maintain(o);
    }
} T;
```

## 平衡树  
### Treap  
操作  
- 1 x 插入x  
- 2 x 删除x， 如果有多个，只删除1个  
- 3 x 查询x的排名  
- 4 x 查询排名为x的数  
- 5 x 查询x的前驱（小于x的数中最大的）  
- 6 x 查询x的后继（大于x的数中最小的）  
```cpp
#include <bits/stdc++.h>

using namespace std;

const int maxn = 100000 + 100;
const int INF = 0x3f3f3f3f;

//二叉搜索树 + 大根椎
struct Treap {

    int lc[maxn], rc[maxn];
    int val[maxn], rnd[maxn];
    int size[maxn], cnt[maxn];
    int tot, root;

    void maintain(int o) {
        size[o] = cnt[o] + size[lc[o]] + size[rc[o]];
    }

    int newnode(int v) {
        tot++;
        val[tot] = v, rnd[tot] = rand();
        size[tot] = cnt[tot] = 1;
        return tot;
    }

    void zig(int& p) { //右旋
        int q = lc[p];
        lc[p] = rc[q];
        rc[q] = p;
        p = q;
        maintain(rc[p]), maintain(p);
    }

    void zag(int& p) { //左旋
        int q = rc[p];
        rc[p] = lc[q];
        lc[q] = p;
        p = q;
        maintain(lc[p]), maintain(p);
    }

    void build() {
        root = newnode(-INF);
        insert(root, INF);
    }

    void insert(int& p, int v) {
        if (!p) {
            p = newnode(v);
            return;
        }
        if (val[p] == v) {
            cnt[p]++;
            size[p]++;
            return;
        }
        if (v < val[p]) {
            insert(lc[p], v);
            if (rnd[lc[p]] > rnd[p])
                zig(p);
        } else {
            insert(rc[p], v);
            if (rnd[rc[p]] > rnd[p])
                zag(p);
        }
        maintain(p);
    }

    void remove(int& p, int v) {
        if (!p) return;
        if (v == val[p]) {
            if (cnt[p] > 1) {
                cnt[p]--;
                size[p]--;
                return;
            }
            if (lc[p] && rc[p]) { //有两个儿子: 将p旋到下层
                if (rnd[lc[p]] > rnd[rc[p]]) {
                    zig(p);
                    remove(rc[p], v);
                } else {
                    zag(p);
                    remove(lc[p], v);
                }
            } else { //只有一个儿子（或没有儿子） ， 直接用儿子替代p
                p = lc[p] | rc[p];
            }
            maintain(p);
            return;
        }

        if (v < val[p]) {
            remove(lc[p], v);
        } else {
            remove(rc[p], v);
        }
        maintain(p);
    }

    int getRankByVal(int& p, int v) {
        if (!p) return 1;
        if (v == val[p])
            return size[lc[p]] + 1;
        else if (v < val[p])
            return getRankByVal(lc[p], v);
        else
            return getRankByVal(rc[p], v) + size[lc[p]] + cnt[p];
    }

    int getValByRank(int& p, int k) {
        if (k <= size[lc[p]])
            return getValByRank(lc[p], k);
        else if (k <= size[lc[p]] + cnt[p])
            return val[p];
        else
            return getValByRank(rc[p], k - size[lc[p]] - cnt[p]);
    }

    int getPre(int v) { //查询前驱
        int cur = root;
        int res = 1; // val[1] = -INF
        while (cur) {
            if (val[cur] < v && val[cur] > val[res]) res = cur;
            cur = val[cur] < v ? rc[cur] : lc[cur];
        }
        return val[res];
    }

    int getNxt(int v) {
        int cur = root;
        int res = 2; // val[2] = INF
        while (cur) {
            if (val[cur] > v && val[cur] < val[res]) res = cur;
            cur = val[cur] > v ? lc[cur] : rc[cur];
        }
        return val[res];
    }
} T;


int main() {
    int m;
    cin >> m;
    int op, x;
    T.build();
    while (m--) {
        cin >> op >> x;
        if (op == 1) {
            T.insert(T.root, x);
        } else if (op == 2) {
            T.remove(T.root, x);
        } else if (op == 3) {
            cout << T.getRankByVal(T.root, x) - 1 << endl;
        } else if (op == 4) {
            cout << T.getValByRank(T.root, x + 1) << endl;
        } else if (op == 5) {
            cout << T.getPre(x) << endl;
        } else if (op == 6) {
            cout << T.getNxt(x) << endl;
        }
    }

    return 0;
}
```

### Splay

##### 文艺平衡树  
初始序列为 1 2 3 ... n  
每次操作输入l r, 将a[l], a[l + 1] ... a[r]翻转  

``` cpp
#include <bits/stdc++.h>

using namespace std;

const int maxn = 100000 + 100;

int n, m;

struct Splay {
    int val[maxn], size[maxn];
    bool rev[maxn];
    int ch[maxn][2], p[maxn];
    int root, tot;

    int newnode(int v) {
        tot++;
        val[tot] = v, size[tot] = 1;
        return tot;
    }

    void maintain(int x) {
        size[x] = size[ch[x][0]] + size[ch[x][1]] + 1;
    }

    void pushdown(int x) {
        if (rev[x]) {
            swap(ch[x][0], ch[x][1]);
            rev[ch[x][0]] ^= 1;
            rev[ch[x][1]] ^= 1;

            rev[x] ^= 1;
        }
    }
    int chk(int x) {
        return ch[p[x]][1] == x;
    }
    //将节点x向上旋一层
    void rotate(int x) {
        int y = p[x], z = p[y];
        int k = chk(x);
        ch[z][chk(y)] = x, p[x] = z;
        ch[y][k] = ch[x][!k], p[ch[y][k]] = y;
        ch[x][!k] = y, p[y] = x;
        maintain(y), maintain(x);
    }
    //将节点x旋到节点k的下面， 如果k=0则将x旋到根
    void splay(int x, int k) {
        while (p[x] != k) {
            int y = p[x], z = p[y];
            if (z != k)
                if (chk(x) == chk(y))
                    rotate(y);
                else
                    rotate(x);
            rotate(x);
        }
        if (!k) root = x;
    }
    //插入一个数 （用于创建初始序列）
    void insert(int v) {
        int cur = root, pa = 0;
        while (cur)
            pa = cur, cur = ch[cur][v > val[cur]];
        cur = newnode(v);
        if (pa) ch[pa][v > val[pa]] = cur, p[cur] = pa;
        splay(cur, 0);
    }
    //查询序列中第k个数对应的节点
    int find_k(int k) {
        int cur = root;
        while (true) {
            pushdown(cur);
            if (k <= size[ch[cur][0]])
                cur = ch[cur][0];
            else if (k <= size[ch[cur][0]] + 1)
                return cur;
            else
                k -= size[ch[cur][0]] + 1, cur = ch[cur][1];
        }
    }
    //打印序列
    void print(int x) {
        pushdown(x);
        if (ch[x][0]) print(ch[x][0]);
        if (val[x] >= 1 && val[x] <= n) printf("%d ", val[x]);
        if (ch[x][1]) print(ch[x][1]);
    }

} T;

int main() {

    scanf("%d%d", &n, &m);
    for (int i = 0; i <= n + 1; i++) // 0 和 n + 1为哨兵
        T.insert(i);

    int l, r;
    while (m--) {
        scanf("%d%d", &l, &r);
        l = T.find_k(l);
        r = T.find_k(r + 2);
        T.splay(l, 0);
        T.splay(r, l);
        T.rev[T.ch[r][0]] ^= 1;
    }
    T.print(T.root);
    printf("\n");

    return 0;
}
```
##### NOI2005维护数列

![img](https://cdn.luogu.com.cn/upload/image_hosting/1iplcmi5.png)

```cpp
#include <algorithm>
#include <cstdio>
#include <cstring>
#include <queue>

#define lc ch[x][0]
#define rc ch[x][1]

using namespace std;

const int maxn = 500000 + 100;
int w[maxn];
struct Splay {
    int ch[maxn][2], p[maxn];
    int val[maxn], sumv[maxn];
    int maxsum[maxn], presum[maxn], sufsum[maxn];
    bool rev[maxn], setd[maxn];
    int size[maxn];
    int root, tot;
    queue<int> buff;

    void flip(int x) {
        rev[x] ^= 1;
        swap(lc, rc);
        swap(presum[x], sufsum[x]);
    }
    void setto(int x, int v) {
        setd[x] = true;
        val[x] = v;
        sumv[x] = v * size[x];
        if (v > 0) {
            maxsum[x] = presum[x] = sufsum[x] = v * size[x];
        } else {
            maxsum[x] = v;
            presum[x] = sufsum[x] = 0;
        }
    }

    void maintain(int x) {
        size[x] = 1 + size[lc] + size[rc];
        sumv[x] = sumv[lc] + sumv[rc] + val[x];
        presum[x] = max(presum[lc], sumv[lc] + val[x] + presum[rc]);
        sufsum[x] = max(sufsum[rc], sumv[rc] + val[x] + sufsum[lc]);
        maxsum[x] = max(max(maxsum[lc], maxsum[rc]), sufsum[lc] + val[x] + presum[rc]);
    }

    void pushdown(int x) {
        if (setd[x]) {
            setd[x] = rev[x] = false;
            if (lc) setto(lc, val[x]);
            if (rc) setto(rc, val[x]);
        }

        if (rev[x]) {
            rev[x] = false;
            if (lc) flip(lc);
            if (rc) flip(rc);
        }
    }

    int chk(int x) {
        return ch[p[x]][1] == x;
    }

    void rotate(int x) {
        int y = p[x], z = p[y];
        int k = chk(x);
        ch[z][chk(y)] = x, p[x] = z;
        ch[y][k] = ch[x][!k], p[ch[y][k]] = y;
        ch[x][!k] = y, p[y] = x;
        maintain(y), maintain(x);
    }

    void splay(int x, int k) {
        while (p[x] != k) {
            int y = p[x], z = p[y];
            if (z != k)
                if (chk(x) == chk(y))
                    rotate(y);
                else
                    rotate(x);
            rotate(x);
        }
        if (!k) root = x;
    }

    int newnode(int v, int _p) {
        int x;
        if (buff.size())
            x = buff.front(), buff.pop();
        else
            x = ++tot;

        lc = rc = 0;
        p[x] = _p;

        size[x] = 1;
        val[x] = sumv[x] = maxsum[x] = v;
        presum[x] = sufsum[x] = v > 0 ? v : 0;
        rev[x] = setd[x] = false;
        return x;
    }

    int get_k(int k) {
        int x = root;
        while (x) {
            pushdown(x);
            if (k <= size[lc])
                x = lc;
            else if (k == size[lc] + 1)
                return x;
            else
                k -= size[lc] + 1, x = rc;
        }
        return -1;
    }

    int build(int l, int r, int _p) {
        int m = l + r >> 1;
        int x = newnode(w[m], _p);
        if (l < m) lc = build(l, m - 1, x);
        if (r > m) rc = build(m + 1, r, x);
        maintain(x);
        return x;
    }
} T;

void dfs(int x) { //回收节点
    if (T.lc) dfs(T.lc);
    if (T.rc) dfs(T.rc);
    T.buff.push(x);
}

int readIn() {
    int x = 0;
    int flag = 1;
    char c = getchar();
    while (c < '0' || c > '9') {
        if (c == '-')
            flag = -flag;
        c = getchar();
    }
    while (c >= '0' && c <= '9') {
        x = x * 10 + c - '0';
        c = getchar();
    }
    return x * flag;
}

int main() {
    int n, m;
    n = readIn(), m = readIn();
    for (int i = 1; i <= n; i++)
        w[i] = readIn();
    w[0] = w[n + 1] = -1e8;
    T.maxsum[0] = -1e8;

    T.root = T.build(0, n + 1, 0);

    char op[20];
    int pos, k, v;
    while (m--) {
        scanf("%s", op);
        if (!strcmp(op, "INSERT")) {
            pos = readIn(), k = readIn();
            for (int i = 1; i <= k; i++)
                w[i] = readIn();
            int l = T.get_k(pos + 1), r = T.get_k(pos + 2);
            T.splay(l, 0), T.splay(r, l);
            T.ch[r][0] = T.build(1, k, r);
            T.maintain(r), T.maintain(l);
        } else if (!strcmp(op, "DELETE")) {
            pos = readIn(), k = readIn();
            int l = T.get_k(pos), r = T.get_k(pos + k + 1);
            T.splay(l, 0), T.splay(r, l);
            dfs(T.ch[r][0]);
            T.ch[r][0] = 0;
            T.maintain(r), T.maintain(l);
        } else if (!strcmp(op, "MAKE-SAME")) {
            pos = readIn(), k = readIn(), v = readIn();
            int l = T.get_k(pos), r = T.get_k(pos + k + 1);
            T.splay(l, 0), T.splay(r, l);
            T.setto(T.ch[r][0], v);
            T.maintain(r), T.maintain(l);
        } else if (!strcmp(op, "REVERSE")) {
            pos = readIn(), k = readIn();
            int l = T.get_k(pos), r = T.get_k(pos + k + 1);
            T.splay(l, 0), T.splay(r, l);
            T.flip(T.ch[r][0]);
            T.maintain(r), T.maintain(l);
        } else if (!strcmp(op, "GET-SUM")) {
            pos = readIn(), k = readIn();
            int l = T.get_k(pos), r = T.get_k(pos + k + 1);
            T.splay(l, 0), T.splay(r, l);
            printf("%d\n", T.sumv[T.ch[r][0]]);
        } else if (!strcmp(op, "MAX-SUM")) {
            printf("%d\n", T.maxsum[T.root]);
        }
    }

    return 0;
}
```

## 树链剖分 （轻重链剖分）
给定一棵树，每个点有1个权值，支持以下4种操作  
1. 将u->v路径上的节点权值加上k  
2. 查询u->v路径上节点权值的和  
3. 将以u和根的子树上每个节点权值加上k  
4. 查询以u为根的子树上每个节点的权值之和  
```cpp
#include <algorithm>
#include <cstdio>
#include <cstring>

#define lc (o << 1)
#define rc (o << 1 | 1)

using namespace std;
typedef long long LL;

const int maxn = 100000 + 10;
const int maxm = 2 * maxn;

int a[maxn];
int to[maxm], nxt[maxm], fir[maxn], tot;

int sz[maxn], son[maxn], dep[maxn], father[maxn];

int top[maxn], pos[maxn], b[maxn]; // 所在重链的顶节点， 在新序列中的位置， 新序列中的值
int cnt;
int n;
void add_edge(int u, int v) {
    tot++;
    to[tot] = v, nxt[tot] = fir[u], fir[u] = tot;
}
/*
dfs1:计算子树大小sz, 重儿子son, 深度dep, 父节点father
*/
void dfs1(int u, int f) {
    sz[u] = 1;
    father[u] = f;
    dep[u] = dep[f] + 1;
    for (int e = fir[u]; e; e = nxt[e]) {
        int v = to[e];
        if (v == f) continue;
        dfs1(v, u);
        sz[u] += sz[v];
        if (sz[son[u]] < sz[v]) son[u] = v;
    }
}
/*
dfs2: 计算节点所在重链的顶端节点 top, 节点在序列中的位置pos, 序列中的值b
*/
void dfs2(int u, int t) {
    top[u] = t;
    pos[u] = ++cnt;
    b[cnt] = a[u];

    if (!son[u]) return;
    dfs2(son[u], t); //重儿子

    for (int e = fir[u]; e; e = nxt[e]) {
        int v = to[e];
        if (v == son[u] || v == father[u]) continue;
        dfs2(v, v);
    }
}

//线段树
struct SegmentTree {
    LL sumv[maxn * 4], addv[maxn * 4];

    void maintain(int o) {
        sumv[o] = sumv[lc] + sumv[rc];
    }

    void pushdown(int o, int L, int R) {
        if (addv[o]) {
            int M = L + R >> 1;
            addv[lc] += addv[o], sumv[lc] += addv[o] * (M - L + 1);
            addv[rc] += addv[o], sumv[rc] += addv[o] * (R - M);
            addv[o] = 0;
        }
    }

    void build(int o, int L, int R) {
        if (L == R) {
            sumv[o] = b[L];
            addv[o] = 0;
            return;
        }
        int M = L + R >> 1;
        build(lc, L, M);
        build(rc, M + 1, R);
        maintain(o);
    }

    void update(int o, int L, int R, int l, int r, LL v) {
        if (l <= L && r >= R) {
            sumv[o] += v * (R - L + 1);
            addv[o] += v;
            return;
        }
        pushdown(o, L, R);
        int M = L + R >> 1;
        if (l <= M) update(lc, L, M, l, r, v);
        if (r > M) update(rc, M + 1, R, l, r, v);
        maintain(o);
    }

    LL query(int o, int L, int R, int l, int r) {
        if (l <= L && r >= R) return sumv[o];
        pushdown(o, L, R);
        int M = L + R >> 1;
        LL res = 0;
        if (l <= M) res += query(lc, L, M, l, r);
        if (r > M) res += query(rc, M + 1, R, l, r);
        return res;
    }

} T;
/*
    将路径转化为O(log(n))个区间
    u -> v
    将u和v中top[i]深的向上跳，直到u和v在同一条重链
*/
void update_path(int u, int v, int k) {
    while (top[u] != top[v]) {
        if (dep[top[u]] < dep[top[v]]) swap(u, v);
        T.update(1, 1, n, pos[top[u]], pos[u], k);
        u = father[top[u]];
    }
    if (dep[u] < dep[v]) swap(u, v);
    T.update(1, 1, n, pos[v], pos[u], k);
}

LL query_path(int u, int v) {
    LL res = 0;
    while (top[u] != top[v]) {
        if (dep[top[u]] < dep[top[v]]) swap(u, v);
        res += T.query(1, 1, n, pos[top[u]], pos[u]);
        u = father[top[u]];
    }
    if (dep[u] < dep[v]) swap(u, v);
    res += T.query(1, 1, n, pos[v], pos[u]);
    return res;
}

void update_tree(int u, int k) {
    T.update(1, 1, n, pos[u], pos[u] + sz[u] - 1, k);
}
LL query_tree(int u) {
    return T.query(1, 1, n, pos[u], pos[u] + sz[u] - 1);
}

int main() {

    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", a + i);
    }
    int op, u, v, k;
    for (int i = 1; i < n; i++) {
        scanf("%d%d", &u, &v);
        add_edge(u, v);
        add_edge(v, u);
    }

    dfs1(1, 0); // sz, son, dep， father
    dfs2(1, 1); // top, pos, b

    T.build(1, 1, n);

    int q;
    scanf("%d", &q);

    while (q--) {

        scanf("%d", &op);

        if (op == 1) {
            scanf("%d%d%d", &u, &v, &k);
            update_path(u, v, k);
        } else if (op == 2) {
            scanf("%d%d", &u, &k);
            update_tree(u, k);
        } else if (op == 3) {
            scanf("%d%d", &u, &v);
            LL res = query_path(u, v);
            printf("%lld\n", res);
        } else if (op == 4) {
            scanf("%d", &u);
            LL res = query_tree(u);
            printf("%lld\n", res);
        }
    }
    return 0;
}
```

# 数学

## 数论
### Baby Step Giant Step算法
Baby Step, Giant Step算法:  
求解不定方程$a^x \equiv b \ (mod \ p) $, $ (a \ p \text{互质})$  
设$x = it - j \ ( 1 \leqslant i \leqslant t, \ 0 \leqslant j \leqslant t ) \quad (t ^ 2 \geqslant p)  $ 
注意$i$不能从0开始，否则会产生负数解  
$j$必须取到$t$， 否则可能漏解 $ x = 0 $  
$ a^{i*t} \equiv b*a^j \ (mod \ p) $  
先枚举右边并存到哈希表，然后枚举左边的值，并在哈希表中查找解。  
```cpp
LL bsgs(LL a, LL b, LL p) {
    b %= p;
    unordered_map<LL, LL> hash;
    LL t = sqrt(p) + 1;
    LL x = 1;
    for (LL j = 0; j < t; j++) {
        LL val = b * x % p;
        hash[val] = j;
        x = x * a % p;
    }
    hash[b * x % p] = t;
    a = x;
    for (LL i = 1; i <= t; i++) {
        if (hash.count(x)) {
            return i * t - hash[x];
        }
        x = x * a % p;
    }
    return -1;
}
```
### 扩展BSGS
题目同上， 但不保证 a p互质  

$a^x = b \ mod \ p$  
1. 先判断 $x = 0$ 是不是解, 是则直接返回  
2. 设 $d = gcd(a, p)$，如果$b \nmid d$,则无解，否则$\frac{a}{d}a^{x - 1} = \frac{b}{d} \ mod \ \frac{p}{d}$  
3. $a^{x - 1} = \frac{b}{d} (\frac{a}{d})^{-1} \ mod \ \frac{p}{d}$，递归求解  

```cpp
#include <bits/stdc++.h>

using namespace std;
typedef long long LL;
const LL INF = 0x3f3f3f3f3f3f3f3f;

LL exgcd(LL a, LL b, LL& x, LL& y) {
    if (b == 0) {
        x = 1, y = 0;
        return a;
    } else {
        LL d = exgcd(b, a % b, y, x);
        y -= a / b * x;
        return d;
    }
}

LL bsgs(LL a, LL b, LL p) {
    b %= p;

    unordered_map<LL, LL> hash;
    LL x = 1;
    LL t = sqrt(p) + 1;

    for (int j = 0; j < t; j++) {
        hash[x * b % p] = j;
        x = x * a % p;
    }
    hash[x * b % p] = t;

    a = x;
    for (int i = 1; i <= t; i++) {
        if (hash.count(x)) return i * t - hash[x];
        x = x * a % p;
    }
    return -INF;
}

LL exbsgs(LL a, LL b, LL p) {
    if (1 % p == b % p) return 0;

    LL x, y, d;
    d = exgcd(a, p, x, y);
    if (d == 1) {
        return bsgs(a, b, p);
    } else {
        if (b % d) return -INF;
        p /= d;
        //a / d 模 p的逆元
        exgcd(a / d, p, x, y);
        x = (x % p + p) % p;
        return 1 + exbsgs(a, b / d * x % p, p);
    }
}

int main() {
    LL a, p, b;
    while (cin >> a >> p >> b, a || p || b) {
        LL res = exbsgs(a, b, p);
        if (res < 0)
            cout << "No Solution" << endl;
        else
            cout << res << endl;
    }

    return 0;
}
```

## 多项式卷积
### 快速傅里叶变换FFT
```cpp
#include <algorithm>
#include <cmath>
#include <cstdio>

using namespace std;

const int maxn = 300000 + 100;
const double pi = acos(-1);

struct Complex {
    double x, y;

    Complex() {
        x = y = 0;
    }
    Complex(double x, double y) : x(x), y(y) {}

    Complex operator+(const Complex& b) const {
        return Complex(x + b.x, y + b.y);
    }

    Complex operator-(const Complex& b) const {
        return Complex(x - b.x, y - b.y);
    }

    Complex operator*(const Complex& b) const {
        return Complex(x * b.x - y * b.y, x * b.y + y * b.x);
    }
} a[maxn], b[maxn];

int rev[maxn];
int n, m, N, l;

void fft(Complex* A, int N, int type) {

    for (int i = 0; i < N; i++)
        if (rev[i] < i)
            swap(A[i], A[rev[i]]);

    for (int len = 2; len <= N; len <<= 1) {
        int m = len >> 1;
        Complex wn(cos(2 * pi / len), type * sin(2 * pi / len));
        for (int j = 0; j < N; j += len) {
            Complex w(1, 0);

            for (int i = 0; i < m; i++) {
                Complex u = A[j + i];
                Complex v = A[j + i + m];

                A[j + i] = u + w * v;
                A[j + i + m] = u - w * v;

                w = w * wn;
            }
        }
    }
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 0; i <= n; i++)
        scanf("%lf", &a[i].x);
    for (int i = 0; i <= m; i++)
        scanf("%lf", &b[i].x);
    N = 1, l = 0;
    while (N < n + m + 1) {
        N <<= 1;
        l++;
    }

    for (int i = 0; i < N; i++)
        rev[i] = rev[i >> 1] >> 1 | (i & 1) << (l - 1);

    fft(a, N, 1);
    fft(b, N, 1);

    for (int i = 0; i < N; i++)
        a[i] = a[i] * b[i];

    fft(a, N, -1);

    for (int i = 0; i <= n + m; i++)
        printf("%d ", (int)(a[i].x / N + 0.5));

    return 0;
}
```

### 快速数论变换 NTT
```cpp
#include <algorithm>
#include <cmath>
#include <cstdio>

using namespace std;

typedef long long LL;

const LL P = 998244353;
const LL g = 3;
const int maxn = 300000 + 100;

int n, m, N, l;

LL a[maxn], b[maxn];
int rev[maxn];

LL pow_mod(LL a, LL b, LL p) {
    LL res = 1;
    while (b) {
        if (b & 1)
            res = res * a % p;
        a = a * a % p;
        b >>= 1;
    }
    return res;
}

void fft(LL* A, int N, int type) {
    for (int i = 0; i < N; i++)
        if (rev[i] < i)
            swap(A[i], A[rev[i]]);

    for (int len = 2; len <= N; len <<= 1) {
        int m = len / 2;
        LL wn = pow_mod(g, (P - 1) / len, P);
        if (type == -1)
            wn = pow_mod(wn, P - 2, P);
        for (int j = 0; j < N; j += len) {
            LL w = 1;

            for (int i = 0; i < m; i++) {
                LL u = A[j + i];
                LL v = A[j + i + m];

                A[j + i] = (u + w * v % P) % P;
                A[j + i + m] = (u - w * v % P + P) % P;

                w = w * wn % P;
            }
        }
    }
}

int main() {

    scanf("%d%d", &n, &m);

    for (int i = 0; i <= n; i++)
        scanf("%lld", a + i);
    for (int i = 0; i <= m; i++)
        scanf("%lld", b + i);

    N = 1, l = 0;
    while (N < n + m + 1) {
        N <<= 1;
        l++;
    }

    for (int i = 0; i < N; i++)
        rev[i] = rev[i >> 1] >> 1 | (i & 1) << (l - 1);

    fft(a, N, 1);
    fft(b, N, 1);

    for (int i = 0; i < N; i++)
        a[i] = a[i] * b[i] % P;

    fft(a, N, -1);

    LL aN = pow_mod(N, P - 2, P);

    for (int i = 0; i <= n + m; i++)
        printf("%lld ", a[i] * aN % P);

    return 0;
}
```
### 多项式求逆
如果$F(x) * G(x) \equiv 1 \ mod \ x^n $ (系数对998255435取模)，则称多项式$G(x)$是多项式$F(x)$的逆。  

如果$F(x)*H(x) \equiv 1 \ mod \ x^{t}$  
$G(x) = 2H(x) - F(x) * H^2(x)$  
则$F(x)*G(x) \equiv 1 \ mod \ x^{2*t}$  
从$t = 1$开始向上递推， 直到$t$不小于$n$  

```cpp
int main() {
    int n;
    n = readIn();

    for (int i = 0; i < n; i++)
        F[i] = readIn();

    G[0] = pow_mod(F[0], P - 2, P);
    int t = 1;
    //G[x] * F[x] = 1 (mod x^t)
    while (t < n) {
        t <<= 1;
        int N = 1, l = 0;

        while (N <= t) {
            N <<= 1;
            l++;
        }

        for (int i = 0; i < N; i++)
            rev[i] = rev[i >> 1] >> 1 | (i & 1) << (l - 1);

        for (int i = 0; i < t; i++)
            T[i] = F[i];
        for (int i = t; i < N; i++)
            T[i] = 0;

        ntt(G, N, 1);
        ntt(T, N, 1);

        for (int i = 0; i < N; i++) {
            G[i] = (2 * G[i] % P - (T[i] * G[i] % P) * G[i] % P + P) % P;
        }

        ntt(G, N, -1);
        LL aN = pow_mod(N, P - 2, P);
        for (int i = 0; i < N; i++) {
            G[i] = G[i] * aN % P;
        }
        for (int i = t; i < N; i++)
            G[i] = 0;
    }

    for (int i = 0; i <= n; i++)
        printf("%lld ", G[i]);
    printf("\n");

    return 0;
}
```

