        dfs(to[e]);
        cnt[u] += cnt[to[e]];
    }
}

void query(char* s) {
    int n = strlen(s);
    int u = 0;
    for (int i = 0; i < n; i++) {
        u = trie[u][s[i] - 'a'];
        cnt[u]++;
    }
    for (int i = 1; i <= tot; i++)
        add(fail[i], i);
    dfs(0);
}

