    scanf("%s", s);
    int len = strlen(s);
    int n = 0;
    ss[n++] = '$', ss[n++] = '#';
    for(int i = 0; i < len; i++)
        ss[n++] = s[i], ss[n ++] = '#';
    ss[n++] = '~', ss[n++] = '\0';

    int id = 0, mx = 1;
    for(int i = 1; i < n; i++) {
        if(i < mx) p[i] = min( mx - i, p[2 * id - i]);
        else p[i] = 1;
        while(ss[i + p[i]] == ss[i - p[i]]) p[i]++;
        if(i + p[i] > mx)
            mx = i + p[i], id = i;
    }
    int res = 0;
    for(int i = 1; i < n; i++)
        res = max(res, p[i] - 1);
    printf("%d\n", res);

    return 0;
}
