## 线段树

### 1. 区间加法， 区间求和
```cpp
struct SegmentTree {
    LL sumv[maxn * 4], addv[maxn * 4]; //原数组大小的四倍

    void maintain(int o) {
        sumv[o] = sumv[lc] + sumv[rc];
    }

    void pushdown(int o,int L,int R) { //标记下传
        int M = L+R>>1;
        addv[lc] += addv[o];
        sumv[lc] += addv[o] *( M-L +1);

        addv[rc] += addv[o];
        sumv[rc] += addv[o] *(R-M);

        addv[o] = 0;

    }

    void build(int o, int L, int R) {
        if (L == R) {
            sumv[o] = v[L];
            return;
        }
        int M = L + R >> 1;
        build(lc, L, M);
        build(rc, M + 1, R);
        maintain(o);
    }

    LL query(int o, int L, int R, int l, int r) {
        if (l <= L && r >= R) return sumv[o];
        pushdown(o,L,R);
        int M = L + R >> 1;
        LL sum = 0;
        if(l <= M) sum += query(lc,L,M,l,r);
        if(r > M) sum += query(rc,M+1,R,l,r);
        return sum;
    }

    void update(int o,int L,int R,int l,int r,LL x){
        if(l <= L && r >= R){
            addv[o] += x;
            sumv[o] += (R-L+1) * x;
            return;
        }
        pushdown(o,L,R);
        int M = L+R>>1;
        if(l <= M) update(lc,L,M,l,r,x);
        if(r > M ) update(rc,M+1,R,l,r,x);
        maintain(o);
    }


} T;
```


### 2. 区间加法， 乘法， 区间求和

```cpp
struct SegmentTree {
    LL sumv[maxn], addv[maxn], mulv[maxn];

    void maintain(int o) {
        sumv[o] = sumv[lc] + sumv[rc];
    }

    void pushdown(int o, int L, int R) {
        int M = L + R >> 1;
        if (mulv[o] != 1) {
            mulv[lc] *= mulv[o];
            addv[lc] *= mulv[o];
            sumv[lc] *= mulv[o];

            mulv[rc] *= mulv[o];
            addv[rc] *= mulv[o];
            sumv[rc] *= mulv[o];

            mulv[o] = 1;
        }
        if (addv[o]) {
            addv[lc] += addv[o];
            sumv[lc] += addv[o] * (M - L + 1);

            addv[rc] += addv[o];
            sumv[rc] += addv[o] * (R - M);

            addv[o] = 0;
        }
    }

    void build(int o, int L, int R) {
        addv[o] = 0;
        mulv[o] = 1;
        if (L == R) {
            sumv[o] = v[L];
            return;
        }
        int M = L + R >> 1;
        build(lc, L, M);
        build(rc, M + 1, R);
        maintain(o);
    }

    LL query(int o, int L, int R, int l, int r) {
        if (l <= L && r >= R) return sumv[o];
        pushdown(o, L, R);
        int M = L + R >> 1;
        LL sum = 0;
        if (l <= M) sum += query(lc, L, M, l, r);
        if (r > M) sum += query(rc, M + 1, R, l, r);
        return sum;
    }

    void update_add(int o, int L, int R, int l, int r, int x) {
        if (l <= L && r >= R) {
            addv[o] += x;
            sumv[o] += x * (R - L + 1);
            return;
        }
        pushdown(o, L, R);
        int M = L + R >> 1;
        if (l <= M) update_add(lc, L, M, l, r, x);
        if (r > M) update_add(rc, M + 1, R, l, r, x);
        maintain(o);
    }

    void update_mul(int o, int L, int R, int l, int r, int x) {
        if (l <= L && r >= R) {
            mulv[o] *= x;
            addv[o] *= x;
            sumv[o] *= x;
            return;
        }
        pushdown(o, L, R);
        int M = L + R >> 1;
        if (l <= M) update_mul(lc, L, M, l, r, x);
        if (r > M) update_mul(rc, M + 1, R, l, r, x);
        maintain(o);
    }
} T;
```

## 平衡树  
### Treap  
操作  
- 1 x 插入x  
- 2 x 删除x， 如果有多个，只删除1个  
- 3 x 查询x的排名  
- 4 x 查询排名为x的数  
- 5 x 查询x的前驱（小于x的数中最大的）  
- 6 x 查询x的后继（大于x的数中最小的）  
```cpp
#include <bits/stdc++.h>

using namespace std;

const int maxn = 100000 + 100;
const int INF = 0x3f3f3f3f;

//二叉搜索树 + 大根椎
struct Treap {

    int lc[maxn], rc[maxn];
    int val[maxn], rnd[maxn];
    int size[maxn], cnt[maxn];
    int tot, root;

    void maintain(int o) {
        size[o] = cnt[o] + size[lc[o]] + size[rc[o]];
    }

    int newnode(int v) {
        tot++;
        val[tot] = v, rnd[tot] = rand();
        size[tot] = cnt[tot] = 1;
        return tot;
    }

    void zig(int& p) { //右旋
        int q = lc[p];
        lc[p] = rc[q];
        rc[q] = p;
        p = q;
        maintain(rc[p]), maintain(p);
    }

    void zag(int& p) { //左旋
        int q = rc[p];
        rc[p] = lc[q];
        lc[q] = p;
        p = q;
        maintain(lc[p]), maintain(p);
    }

    void build() {
        root = newnode(-INF);
        insert(root, INF);
    }

    void insert(int& p, int v) {
        if (!p) {
            p = newnode(v);
            return;
        }
        if (val[p] == v) {
            cnt[p]++;
            size[p]++;
            return;
        }
        if (v < val[p]) {
            insert(lc[p], v);
            if (rnd[lc[p]] > rnd[p])
                zig(p);
        } else {
            insert(rc[p], v);
            if (rnd[rc[p]] > rnd[p])
                zag(p);
        }
        maintain(p);
    }

    void remove(int& p, int v) {
        if (!p) return;
        if (v == val[p]) {
            if (cnt[p] > 1) {
                cnt[p]--;
                size[p]--;
                return;
            }
            if (lc[p] && rc[p]) { //有两个儿子: 将p旋到下层
                if (rnd[lc[p]] > rnd[rc[p]]) {
                    zig(p);
                    remove(rc[p], v);
                } else {
                    zag(p);
                    remove(lc[p], v);
                }
            } else { //只有一个儿子（或没有儿子） ， 直接用儿子替代p
                p = lc[p] | rc[p];
            }
            maintain(p);
            return;
        }

        if (v < val[p]) {
            remove(lc[p], v);
        } else {
            remove(rc[p], v);
        }
        maintain(p);
    }

    int getRankByVal(int& p, int v) {
        if (!p) return 1;
        if (v == val[p])
            return size[lc[p]] + 1;
        else if (v < val[p])
            return getRankByVal(lc[p], v);
        else
            return getRankByVal(rc[p], v) + size[lc[p]] + cnt[p];
    }

    int getValByRank(int& p, int k) {
        if (k <= size[lc[p]])
            return getValByRank(lc[p], k);
        else if (k <= size[lc[p]] + cnt[p])
            return val[p];
        else
            return getValByRank(rc[p], k - size[lc[p]] - cnt[p]);
    }

    int getPre(int v) { //查询前驱
        int cur = root;
        int res = 1; // val[1] = -INF
        while (cur) {
            if (val[cur] < v && val[cur] > val[res]) res = cur;
            cur = val[cur] < v ? rc[cur] : lc[cur];
        }
        return val[res];
    }

    int getNxt(int v) {
        int cur = root;
        int res = 2; // val[2] = INF
        while (cur) {
            if (val[cur] > v && val[cur] < val[res]) res = cur;
            cur = val[cur] > v ? lc[cur] : rc[cur];
        }
        return val[res];
    }
} T;


int main() {
    int m;
    cin >> m;
    int op, x;
    T.build();
    while (m--) {
        cin >> op >> x;
        if (op == 1) {
            T.insert(T.root, x);
        } else if (op == 2) {
            T.remove(T.root, x);
        } else if (op == 3) {
            cout << T.getRankByVal(T.root, x) - 1 << endl;
        } else if (op == 4) {
            cout << T.getValByRank(T.root, x + 1) << endl;
        } else if (op == 5) {
            cout << T.getPre(x) << endl;
        } else if (op == 6) {
            cout << T.getNxt(x) << endl;
        }
    }

    return 0;
}
```

### Splay

##### 文艺平衡树  
初始序列为 1 2 3 ... n  
每次操作输入l r, 将a[l], a[l + 1] ... a[r]翻转  

``` cpp
#include <bits/stdc++.h>

using namespace std;

const int maxn = 100000 + 100;

int n, m;

struct Splay {
    int val[maxn], size[maxn];
    bool rev[maxn];
    int ch[maxn][2], p[maxn];
    int root, tot;

    int newnode(int v) {
        tot++;
        val[tot] = v, size[tot] = 1;
        return tot;
    }

    void maintain(int x) {
        size[x] = size[ch[x][0]] + size[ch[x][1]] + 1;
    }

    void pushdown(int x) {
        if (rev[x]) {
            swap(ch[x][0], ch[x][1]);
            rev[ch[x][0]] ^= 1;
            rev[ch[x][1]] ^= 1;

            rev[x] ^= 1;
        }
    }
    int chk(int x) {
        return ch[p[x]][1] == x;
    }
    //将节点x向上旋一层
    void rotate(int x) {
        int y = p[x], z = p[y];
        int k = chk(x);
        ch[z][chk(y)] = x, p[x] = z;
        ch[y][k] = ch[x][!k], p[ch[y][k]] = y;
        ch[x][!k] = y, p[y] = x;
        maintain(y), maintain(x);
    }
    //将节点x旋到节点k的下面， 如果k=0则将x旋到根
    void splay(int x, int k) {
        while (p[x] != k) {
            int y = p[x], z = p[y];
            if (z != k)
                if (chk(x) == chk(y))
                    rotate(y);
                else
                    rotate(x);
            rotate(x);
        }
        if (!k) root = x;
    }
    //插入一个数 （用于创建初始序列）
    void insert(int v) {
        int cur = root, pa = 0;
        while (cur)
            pa = cur, cur = ch[cur][v > val[cur]];
        cur = newnode(v);
        if (pa) ch[pa][v > val[pa]] = cur, p[cur] = pa;
        splay(cur, 0);
    }
    //查询序列中第k个数对应的节点
    int find_k(int k) {
        int cur = root;
        while (true) {
            pushdown(cur);
            if (k <= size[ch[cur][0]])
                cur = ch[cur][0];
            else if (k <= size[ch[cur][0]] + 1)
                return cur;
            else
                k -= size[ch[cur][0]] + 1, cur = ch[cur][1];
        }
    }
    //打印序列
    void print(int x) {
        pushdown(x);
        if (ch[x][0]) print(ch[x][0]);
        if (val[x] >= 1 && val[x] <= n) printf("%d ", val[x]);
        if (ch[x][1]) print(ch[x][1]);
    }

} T;

int main() {

    scanf("%d%d", &n, &m);
    for (int i = 0; i <= n + 1; i++) // 0 和 n + 1为哨兵
        T.insert(i);

    int l, r;
    while (m--) {
        scanf("%d%d", &l, &r);
        l = T.find_k(l);
        r = T.find_k(r + 2);
        T.splay(l, 0);
        T.splay(r, l);
        T.rev[T.ch[r][0]] ^= 1;
    }
    T.print(T.root);
    printf("\n");

    return 0;
}
